(window.webpackJsonp=window.webpackJsonp||[]).push([[43],{999:function(n,e,o){"use strict";o.r(e),e.default="# ConfigProvider \u5168\u5c40\u914d\u7f6e\n\n\n\n## \u57fa\u672c\u7528\u6cd5\n```jsx\nimport { useState } from 'react';\nimport { ConfigProvider, Cell, Button, SearchBar, Modal, Keyboard, Radio, Message } from 'zarm';\nimport enUS from 'zarm/config-provider/locale/en_US';\nimport zhCN from 'zarm/config-provider/locale/zh_CN';\n\nconst Demo = () => {\n  const [locale, setLocale] = useState(GlobalContext.locale);\n  const [theme, setTheme] = useState(GlobalContext.theme);\n  const [primaryColor, setPrimaryColor] = useState(GlobalContext.primaryColor);\n\n  const show = (key) => {\n    if (key === 'alert') {\n      Modal.alert({\n        title: '\u8b66\u544a',\n        content: '\u8fd9\u91cc\u662f\u8b66\u544a\u4fe1\u606f',\n        shape: 'radius'\n      });\n    } else {\n      Modal.confirm({\n        title: '\u786e\u8ba4\u4fe1\u606f',\n        content: '\u4f60\u786e\u5b9a\u8981\u8fd9\u6837\u505a\u5417\uff1f',\n        shape: 'radius'\n      })\n    }\n  };\n\n  return (\n    <>\n      <Cell\n        title=\"\u5207\u6362\u8bed\u8a00\"\n        description={\n          <Radio.Group\n            compact\n            type=\"button\"\n            value={locale}\n            onChange={setLocale}\n          >\n            <Radio value=\"zhCN\">\u4e2d\u6587</Radio>\n            <Radio value=\"enUS\">EN</Radio>\n          </Radio.Group>\n        }\n      />\n\n      <Cell\n        title=\"\u5207\u6362\u54c1\u724c\u8272\"\n        description={\n          <ul className=\"colors\">\n            {\n              ['#00bc70', '#1890ff', '#f5222d', '#fa541b', '#13c2c2', '#2f54ec', '#712fd1'].map((color, index) => {\n                return (\n                  <li\n                    key={+index}\n                    style={{ backgroundColor: color }}\n                    onClick={() => setPrimaryColor(color)}\n                  />\n                );\n              })\n            }\n          </ul>\n        }\n      />\n\n      <Cell\n        title=\"\u5207\u6362\u4e3b\u9898\"\n        description={\n          <Radio.Group\n            compact\n            type=\"button\"\n            value={theme}\n            onChange={(value) => setTheme(value)}\n          >\n            <Radio value=\"light\">\u9ed8\u8ba4\u4e3b\u9898</Radio>\n            <Radio value=\"dark\">\u6697\u9ed1\u4e3b\u9898</Radio>\n          </Radio.Group>\n        }\n      />\n\n      <Message theme=\"warning\">\u4ee5\u4e0b\u4e3a\u7ec4\u4ef6\u793a\u4f8b</Message>\n\n      <ConfigProvider locale={locale === 'enUS' ? enUS : zhCN} primaryColor={primaryColor} theme={theme}>\n        <>\n          <SearchBar />\n          <Keyboard />\n\n          <Cell\n            description={\n              <Button size=\"xs\" onClick={() => show('alert')}>\u5f00\u542f</Button>\n            }\n          >\n            \u8b66\u544a\u6846\n          </Cell>\n\n          <Cell\n            description={\n              <Button size=\"xs\" onClick={() => show('confirm')}>\u5f00\u542f</Button>\n            }\n          >\n            \u786e\u8ba4\u6846\n          </Cell>\n        </>\n      </ConfigProvider>\n    </>\n  );\n}\n\nReactDOM.render(<Demo />, mountNode);\n```\n\n\n\n## API\n\n| \u5c5e\u6027 | \u7c7b\u578b | \u9ed8\u8ba4\u503c | \u8bf4\u660e |\n| :--- | :--- | :--- | :--- |\n| locale | Object | - | \u8bed\u8a00\u5305\u914d\u7f6e\uff0c\u9ed8\u8ba4\u4e3a\u4e2d\u6587\uff0c\u8bed\u8a00\u5305\u53ef\u5230 [zarm/lib/config-provider/locale](https://unpkg.com/zarm/lib/config-provider/locale/) \u76ee\u5f55\u4e0b\u5bfb\u627e |\n| theme | 'light' \\| 'dark' | 'light' | \u4e3b\u9898\u6a21\u5f0f\uff0c\u5149\u4eae\u4e3b\u9898 \u548c \u6697\u9ed1\u4e3b\u9898\u7684\u5207\u6362 |\n| primaryColor | string | '#00bc70' | \u54c1\u724c\u6807\u51c6\u8272 |"}}]);
